import React, { useState } from "react";
import { Form, Container } from "react-bulma-components";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
const { Input, Field, Control, Label } = Form;

function BookForm() {
  const [startDate, setStartDate] = useState(new Date());
  const [name, setName] = useState("");
  const onChangeDay = ({ target: { value } }) => {
    setStartDate(value);
    console.log(startDate);
  };
  const onChangeName = ({ target: { value } }) => {
    setName(value);
    console.log(name);
  };

  return (
    <Container>
      <Field>
        <Label>Nome</Label>
        <Control>
          <Input name="name" onChange={onChangeName} value={name} />
        </Control>
        <Label>Data de nascimento</Label>
        <Control>
          <Input></Input>
        </Control>
      </Field>
      <Field>
        {startDate}
        <h2>Agendamento</h2>
        <Label>Dia de vacinação</Label>
        <Control>
          <DatePicker onChange={onChangeDay} value={startDate.getDay()} />
        </Control>
        <Label>Horário</Label>
        <Input />
      </Field>
    </Container>
  );
}

export default BookForm;
