import React from "react";
import { Button } from "react-bulma-components";
import { Formik, Form, Field, ErrorMessage } from "formik";
import DatePicker from "react-datepicker";
import "./index.css";
import "react-datepicker/dist/react-datepicker.css";
import axios from "../../utils/api";

function BookForm() {
  const initialValues = { name: "", birthDay: null, date: null };

  const onSubmit = async (values) => {
    const { name, birthDay, date } = values;
    console.log(name);
    try {
      const res = await axios.post("/book", values);
      console.log(res.data);
    } catch (e) {
      console.log(e);
    }
  };
  function validate(values) {
    const errors = {};
    if (!values.name) {
      errors.name = "Required";
    }
    if (!values.birthDay) {
      errors.birthDay = "Required";
    }
    if (!values.date) {
      errors.date = "Required";
    }
    return errors;
  }
  return (
    <Formik
      initialValues={initialValues}
      validate={validate}
      onSubmit={onSubmit}
    >
      <Form>
        <label className="label">Nome Completo</label>
        <Field type="text" name="name" className="input"></Field>
        <ErrorMessage name="name" component="div" />
        <label className="label">Data de Nascimento</label>
        <Field name="birthDay">
          {({ form, field }) => {
            const { setFieldValue } = form;
            const { value } = field;
            return (
              <DatePicker
                className="input"
                dateFormat="dd/MMM/yy"
                maxDate={new Date()}
                id="birthDay"
                {...field}
                selected={value}
                onChange={(val) => setFieldValue("birthDay", val)}
                showMonthDropdown
                showYearDropdown
              />
            );
          }}
        </Field>
        <ErrorMessage name="birthDay" component="div" />
        <label className="label">Data de Vacinação</label>
        <Field name="date" placeHolder="Data de vacinação">
          {({ form, field }) => {
            const { setFieldValue } = form;
            const { value } = field;
            return (
              <DatePicker
                className="input"
                dateFormat="dd/MMM/yyyy h:mm aa"
                timeFormat="HH:mm"
                timeIntervals={15}
                showTimeSelect
                minDate={new Date()}
                id="date"
                {...field}
                selected={value}
                onChange={(val) => setFieldValue("date", val)}
              />
            );
          }}
        </Field>
        <ErrorMessage name="date" component="div" />
        <Button type="submit" color="info" className="box-button is-medium">
          Submit
        </Button>
      </Form>
    </Formik>
  );
}

export default BookForm;
