import React, { useState } from "react";
import { Form, Container } from "react-bulma-components";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
const { Input, Field, Control, Label } = Form;

function BookForm() {
  const [birthDay, setBirthDay] = useState(new Date().toDateString());
  const [date, setDate] = useState(new Date());
  const [name, setName] = useState("");

  const onChangeDay = ({ target: { value } }) => {
    setDate(value);
    console.log(date);
  };

  const onChangeName = ({ target: { value } }) => {
    setName(value);
    console.log(name);
  };

  return (
    <Container>
      <Field>
        <Label>Nome {name}</Label>
        <Control>
          <Input name="name" onChange={onChangeName} value={name} />
        </Control>
        <Label>Data de nascimento</Label>
        <Control>
          <DatePicker value={birthDay} onChange={onChangeDay} type="text" />
        </Control>
      </Field>
      <Field>
        <h2>Agendamento</h2>
        <Label>Dia de vacinação</Label>
        <Control>
          <DatePicker
            onChange={onChangeDay}
            value={date.toISOString()}
            type="date"
          />
        </Control>
        <Label>Horário</Label>
      </Field>
    </Container>
  );
}

export default BookForm;
